import React, { useState } from 'react';
import axios from 'axios';
import './EventPage.css';

const EventPage = () => {
  const [events, setEvents] = useState([
    {
      id: 1, // Example ID for events
      title: 'Event 1',
      date: '2024-08-15',
      location: 'New York, NY',
      description: 'This is the description for Event 1.',
      guests: [
        { name: 'John', surname: 'Doe', phone: '123-456-7890', confirmation: 'Confirmed' },
        { name: 'Jane', surname: 'Smith', phone: '098-765-4321', confirmation: 'Pending' }
      ],
      menu: ['Dish 1', 'Dish 2', 'Dish 3'],
      tasks: ['Task 1', 'Task 2', 'Task 3'],
    },
    // Other initial events...
  ]);

  const [selectedEvent, setSelectedEvent] = useState(events[0]);
  const [activeSections, setActiveSections] = useState({
    details: false,
    guests: false,
    menu: false,
    tasks: false,
  });

  const [isEditing, setIsEditing] = useState(false);
  const [editingSection, setEditingSection] = useState(null);
  const [newSectionContent, setNewSectionContent] = useState("");

  const handleEventSelection = (event) => {
    setSelectedEvent(event);
    setActiveSections({ details: false, guests: false, menu: false, tasks: false });
    setIsEditing(false);
    setEditingSection(null);
  };

  const toggleSection = (section) => {
    setActiveSections(prevState => ({
      ...prevState,
      [section]: !prevState[section]
    }));
  };

  const handleEditSection = (section, content) => {
    setEditingSection(section);
    setNewSectionContent(content.join("\n")); // Handle arrays as new line-separated strings
    setIsEditing(true);
  };

    const handleDeleteEvent = async () => {
    try {
      const response = await axios.delete(`https://your-api-endpoint.com/events/${selectedEvent.id}`);
      if (response.status === 200) {
        setEvents(events.filter(event => event.id !== selectedEvent.id));
        setSelectedEvent(events[0] || null); // Select the first event or null if none are left
      }
    } catch (error) {
      console.error('Error deleting the event:', error);
    }
  };

  const handleSaveSection = async () => {
    try {
      const updatedEvent = { ...selectedEvent };
      if (Array.isArray(selectedEvent[editingSection])) {
        updatedEvent[editingSection] = newSectionContent.split("\n"); // Convert back to array
      } else {
        updatedEvent[editingSection] = newSectionContent;
      }

      const response = await axios.put(`https://your-api-endpoint.com/events/${selectedEvent.id}`, updatedEvent);
      if (response.status === 200) {
        setEvents(events.map(event => event.id === selectedEvent.id ? updatedEvent : event));
        setSelectedEvent(updatedEvent);
        setIsEditing(false);
        setEditingSection(null);
      }
    } catch (error) {
      console.error('Error saving the section:', error);
    }
  };

  const handleChangeSection = (e) => {
    setNewSectionContent(e.target.value);
  };

  return (
    <div className="event-page-container">
      <div className="event-menu">
        <h3>Events</h3>
        <ul>
          {events.map((event, index) => (
            <li key={index} onClick={() => handleEventSelection(event)}>
              {event.title}
            </li>
          ))}
        </ul>
        <button className="add-event-button" onClick={() => handleEditSection('title', '')}>Add Event</button>
      </div>
      <div className="event-details">
        <h1>{selectedEvent.title}</h1>
        <p className="event-subtitle">
          <strong>Date:</strong> {selectedEvent.date} | <strong>Location:</strong> {selectedEvent.location}
        </p>
        <div className="accordion">
          <div className="accordion-section">
            <div className="accordion-header" onClick={() => toggleSection('details')}>
              Details
            </div>
            {activeSections.details && (
              <div className="accordion-content">
                {editingSection === 'description' ? (
                  <div>
                    <textarea
                      value={newSectionContent}
                      onChange={handleChangeSection}
                      rows={5}
                    />
                    <button onClick={handleSaveSection}>Save</button>
                  </div>
                ) : (
                  <div>
                    <p>{selectedEvent.description}</p>
                    <button onClick={() => handleEditSection('description', [selectedEvent.description])}>Edit</button>
                  </div>
                )}
              </div>
            )}
          </div>
          <div className="accordion-section">
            <div className="accordion-header" onClick={() => toggleSection('guests')}>
              Guests
            </div>
            {activeSections.guests && (
              <div className="accordion-content">
                <h3>Guests List</h3>
                <table className="guests-table">
                  <thead>
                    <tr>
                      <th>Name</th>
                      <th>Surname</th>
                      <th>Phone</th>
                      <th>Confirmation</th>
                    </tr>
                  </thead>
                  <tbody>
                    {selectedEvent.guests.map((guest, index) => (
                      <tr key={index}>
                        <td>{guest.name}</td>
                        <td>{guest.surname}</td>
                        <td>{guest.phone}</td>
                        <td>{guest.confirmation}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
                <button onClick={() => handleEditSection('guests', selectedEvent.guests)}>Edit</button>
              </div>
            )}
          </div>
          <div className="accordion-section">
            <div className="accordion-header" onClick={() => toggleSection('menu')}>
              Menu
            </div>
            {activeSections.menu && (
              <div className="accordion-content">
                {editingSection === 'menu' ? (
                  <div>
                    <textarea
                      value={newSectionContent}
                      onChange={handleChangeSection}
                      rows={5}
                    />
                    <button onClick={handleSaveSection}>Save</button>
                  </div>
                ) : (
                  <div>
                    <h3>Menu</h3>
                    <ul>
                      {selectedEvent.menu.map((dish, index) => (
                        <li key={index}>{dish}</li>
                      ))}
                    </ul>
                    <button onClick={() => handleEditSection('menu', selectedEvent.menu)}>Edit</button>
                  </div>
                )}
              </div>
            )}
          </div>
          <div className="accordion-section">
            <div className="accordion-header" onClick={() => toggleSection('tasks')}>
              Tasks
            </div>
            {activeSections.tasks && (
              <div className="accordion-content">
                {editingSection === 'tasks' ? (
                  <div>
                    <textarea
                      value={newSectionContent}
                      onChange={handleChangeSection}
                      rows={5}
                    />
                    <button onClick={handleSaveSection}>Save</button>
                  </div>
                ) : (
                  <div>
                    <h3>Task List</h3>
                    <ul>
                      {selectedEvent.tasks.map((task, index) => (
                        <li key={index}>{task}</li>
                      ))}
                    </ul>
                    <button onClick={() => handleEditSection('tasks', selectedEvent.tasks)}>Edit</button>
                  </div>
                )}
              </div>
            )}
          </div>
        </div>
        <button className="delete-event-button" onClick={handleDeleteEvent}>Delete Event</button>
      </div>
    </div>
  );
};

export default EventPage;
