import React, { useState } from 'react';
import axios from 'axios';
import './EventPage.css';

const EventPage = () => {
  const [events, setEvents] = useState([
    {
      title: 'Event 1',
      date: '2024-08-15',
      location: 'New York, NY',
      description: 'This is the description for Event 1.',
      guests: ['Guest 1', 'Guest 2', 'Guest 3'],
      menu: ['Dish 1', 'Dish 2', 'Dish 3'],
      tasks: ['Task 1', 'Task 2', 'Task 3'],
    },
    // Other initial events...
  ]);

  const [selectedEvent, setSelectedEvent] = useState(events[0]);
  const [activeSections, setActiveSections] = useState({
    details: false,
    guests: false,
    menu: false,
    tasks: false,
  });

  const [isEditing, setIsEditing] = useState(false);
  const [newEvent, setNewEvent] = useState({ ...selectedEvent });

  const handleEventSelection = (event) => {
    setSelectedEvent(event);
    setActiveSections({ details: false, guests: false, menu: false, tasks: false });
    setIsEditing(false);
  };

  const toggleSection = (section) => {
    setActiveSections(prevState => ({
      ...prevState,
      [section]: !prevState[section]
    }));
  };

  const handleAddEvent = () => {
    setNewEvent({
      title: '',
      date: '',
      location: '',
      description: '',
      guests: [],
      menu: [],
      tasks: [],
    });
    setIsEditing(true);
  };

  const handleEditSection = (section) => {
    setIsEditing(section);
  };

  const handleSaveEvent = async () => {
    try {
      let updatedEvents;
      if (newEvent.title) {
        // Add new event
        const response = await axios.post('https://your-api-endpoint.com/events', newEvent);
        if (response.status === 201) {
          updatedEvents = [...events, response.data];
        }
      } else {
        // Edit existing event
        const response = await axios.put(`https://your-api-endpoint.com/events/${selectedEvent.id}`, newEvent);
        if (response.status === 200) {
          updatedEvents = events.map(event => event.id === selectedEvent.id ? response.data : event);
        }
      }
      setEvents(updatedEvents);
      setSelectedEvent(newEvent);
      setIsEditing(false);
    } catch (error) {
      console.error('Error saving the event:', error);
    }
  };

  const handleDeleteEvent = async () => {
    try {
      const response = await axios.delete(`https://your-api-endpoint.com/events/${selectedEvent.id}`);
      if (response.status === 200) {
        setEvents(events.filter(event => event.id !== selectedEvent.id));
        setSelectedEvent(events[0]);
      }
    } catch (error) {
      console.error('Error deleting the event:', error);
    }
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewEvent(prevState => ({
      ...prevState,
      [name]: value,
    }));
  };

  return (
    <div className="event-page-container">
      <div className="event-menu">
        <h3>Events</h3>
        <ul>
          {events.map((event, index) => (
            <li key={index} onClick={() => handleEventSelection(event)}>
              {event.title}
            </li>
          ))}
        </ul>
        <button className="add-event-button" onClick={handleAddEvent}>Add Event</button>
        <button className="delete-event-button" onClick={handleDeleteEvent}>Delete Event</button>
      </div>
      <div className="event-details">
        {!isEditing ? (
          <>
            <h1>{selectedEvent.title}</h1>
            <p className="event-subtitle">
              <strong>Date:</strong> {selectedEvent.date} | <strong>Location:</strong> {selectedEvent.location}
            </p>
            <div className="accordion">
              <div className="accordion-section">
                <div className="accordion-header" onClick={() => toggleSection('details')}>
                  Details
                </div>
                {activeSections.details && (
                  <div className="accordion-content">
                    <p>{selectedEvent.description}</p>
                    <button onClick={() => handleEditSection('details')}>Edit</button>
                  </div>
                )}
              </div>
              <div className="accordion-section">
                <div className="accordion-header" onClick={() => toggleSection('guests')}>
                  Guests
                </div>
                {activeSections.guests && (
                  <div className="accordion-content">
                    <h3>Guests List</h3>
                    <ul>
                      {selectedEvent.guests.map((guest, index) => (
                        <li key={index}>{guest}</li>
                      ))}
                    </ul>
                    <button onClick={() => handleEditSection('guests')}>Edit</button>
                  </div>
                )}
              </div>
              <div className="accordion-section">
                <div className="accordion-header" onClick={() => toggleSection('menu')}>
                  Menu
                </div>
                {activeSections.menu && (
                  <div className="accordion-content">
                    <h3>Menu</h3>
                    <ul>
                      {selectedEvent.menu.map((dish, index) => (
                        <li key={index}>{dish}</li>
                      ))}
                    </ul>
                    <button onClick={() => handleEditSection('menu')}>Edit</button>
                  </div>
                )}
              </div>
              <div className="accordion-section">
                <div className="accordion-header" onClick={() => toggleSection('tasks')}>
                  Tasks
                </div>
                {activeSections.tasks && (
                  <div className="accordion-content">
                    <h3>Task List</h3>
                    <ul>
                      {selectedEvent.tasks.map((task, index) => (
                        <li key={index}>{task}</li>
                      ))}
                    </ul>
                    <button onClick={() => handleEditSection('tasks')}>Edit</button>
                  </div>
                )}
              </div>
            </div>
          </>
        ) : (
          <div className="add-event-form">
            <h2>{newEvent.title ? 'Edit Event' : 'Add New Event'}</h2>
            <input
              type="text"
              name="title"
              placeholder="Event Title"
              value={newEvent.title}
              onChange={handleChange}
            />
            <input
              type="date"
              name="date"
              value={newEvent.date}
              onChange={handleChange}
            />
            <input
              type="text"
              name="location"
              placeholder="Location"
              value={newEvent.location}
              onChange={handleChange}
            />
            <textarea
              name="description"
              placeholder="Description"
              value={newEvent.description}
              onChange={handleChange}
            />
            <button className="save-event-button" onClick={handleSaveEvent}>Save Event</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default EventPage;
