import React, { useState } from 'react';
import './EventPage.css';

const EventPage = () => {
  const [selectedEvent, setSelectedEvent] = useState({
    title: 'Event 1',
    date: '2024-08-15',
    location: 'New York, NY',
    description: 'This is the description for Event 1.'
  });

  const events = [
    { title: 'Event 1', date: '2024-08-15', location: 'New York, NY', description: 'This is the description for Event 1.' },
    { title: 'Event 2', date: '2024-09-05', location: 'Los Angeles, CA', description: 'This is the description for Event 2.' },
    { title: 'Event 3', date: '2024-10-10', location: 'Chicago, IL', description: 'This is the description for Event 3.' }
  ];

  const handleEventSelection = (event) => {
    setSelectedEvent(event);
  };

  return (
    <div className="event-page-container">
      <div className="event-menu">
        <h3>Events</h3>
        <ul>
          {events.map((event, index) => (
            <li key={index} onClick={() => handleEventSelection(event)}>
              {event.title}
            </li>
          ))}
        </ul>
      </div>
      <div className="event-details">
        <h1>{selectedEvent.title}</h1>
        <p><strong>Date:</strong> {selectedEvent.date}</p>
        <p><strong>Location:</strong> {selectedEvent.location}</p>
        <p>{selectedEvent.description}</p>
      </div>
    </div>
  );
};

export default EventPage;
