import React, { useState } from 'react';
import './EventPage.css';

const EventPage = () => {
  const [selectedEvent, setSelectedEvent] = useState({
    title: 'Event 1',
    date: '2024-08-15',
    location: 'New York, NY',
    description: 'This is the description for Event 1.',
    guests: ['Guest 1', 'Guest 2', 'Guest 3'],
    menu: ['Dish 1', 'Dish 2', 'Dish 3'],
  });

  const [activeSection, setActiveSection] = useState('details');

  const events = [
    {
      title: 'Event 1',
      date: '2024-08-15',
      location: 'New York, NY',
      description: 'This is the description for Event 1.',
      guests: ['Guest 1', 'Guest 2', 'Guest 3'],
      menu: ['Dish 1', 'Dish 2', 'Dish 3'],
    },
    {
      title: 'Event 2',
      date: '2024-09-05',
      location: 'Los Angeles, CA',
      description: 'This is the description for Event 2.',
      guests: ['Guest A', 'Guest B', 'Guest C'],
      menu: ['Dish A', 'Dish B', 'Dish C'],
    },
    {
      title: 'Event 3',
      date: '2024-10-10',
      location: 'Chicago, IL',
      description: 'This is the description for Event 3.',
      guests: ['Guest X', 'Guest Y', 'Guest Z'],
      menu: ['Dish X', 'Dish Y', 'Dish Z'],
    }
  ];

  const handleEventSelection = (event) => {
    setSelectedEvent(event);
    setActiveSection('details'); // Reset to the first section
  };

  return (
    <div className="event-page-container">
      <div className="event-menu">
        <h3>Events</h3>
        <ul>
          {events.map((event, index) => (
            <li key={index} onClick={() => handleEventSelection(event)}>
              {event.title}
            </li>
          ))}
        </ul>
      </div>
      <div className="event-details">
        <h1>{selectedEvent.title}</h1>
        <div className="event-sections">
          <button onClick={() => setActiveSection('details')}>Details</button>
          <button onClick={() => setActiveSection('guests')}>Guests</button>
          <button onClick={() => setActiveSection('menu')}>Menu</button>
        </div>
        <div className="event-content">
          {activeSection === 'details' && (
            <div>
              <p><strong>Date:</strong> {selectedEvent.date}</p>
              <p><strong>Location:</strong> {selectedEvent.location}</p>
              <p>{selectedEvent.description}</p>
            </div>
          )}
          {activeSection === 'guests' && (
            <div>
              <h3>Guests List</h3>
              <ul>
                {selectedEvent.guests.map((guest, index) => (
                  <li key={index}>{guest}</li>
                ))}
              </ul>
            </div>
          )}
          {activeSection === 'menu' && (
            <div>
              <h3>Menu</h3>
              <ul>
                {selectedEvent.menu.map((dish, index) => (
                  <li key={index}>{dish}</li>
                ))}
              </ul>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default EventPage;
